<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Common_Model extends CI_Model {	public function __construct()	{		$this->load->database();	}	public function getAll($tablename,$where='',$orderby='',$column='')	{		if($where!=''){ $this->db->where($where); }		if($orderby!=''){ $this->db->order_by($column,$orderby); }		$query = $this->db->get($tablename);		return $query;	}	public function insert($tablename,$data) 	{ 		$this->db->insert($tablename,$data);		return $this->db->insert_id();	} 	public function update($tablename,$data,$where)	{ 		$this->db->where($where); 		$query = $this->db->update($tablename,$data); 		return $this->db->affected_rows();	} 	public function delete($tablename,$where) 	{ 		$this->db->where($where); 		$query = $this->db->delete($tablename); 		return $query;	}}?>